mapper.select:
SELECT COUNT(mainBbsNo) as totalCount from( SELECT A.mainBbsNo FROM MAIN_BBS A, SUB_BBS B, (SELECT @RNUM := 0 ) AS ROWNUM WHERE A.mainBbsNo = B.mainBbsNo AND A.isPublic ='Y' AND A.bbsType1 = '2'
AND A.bbsType2 = #{bbsType2}
ORDER BY A.mainBbsNo DESC )TEMP_T

SELECT /* selectWebFAQList */ totalCount ,mainBbsNo ,bbsType1 ,bbsType2 ,writerNo ,title ,content ,viewCount ,createTime ,updateTime ,ROWNUM from( SELECT (SELECT COUNT(*) AS total_count FROM board) AS totalCount ,A.mainBbsNo ,A.bbsType1 ,A.bbsType2 ,A.writerNo ,A.title ,A.content ,A.viewCount ,A.createTime ,A.updateTime ,(@RNUM := @RNUM + 1 ) AS ROWNUM FROM MAIN_BBS A, SUB_BBS B, (SELECT @RNUM := 0 ) AS ROWNUM WHERE A.mainBbsNo = B.mainBbsNo AND A.isPublic ='Y' AND A.bbsType1 = '2'
AND A.bbsType2 = #{bbsType2}
ORDER BY A.mainBbsNo DESC )TEMP_T WHERE ROWNUM BETWEEN #{S_SEQ} AND #{E_SEQ} ORDER BY ROWNUM ASC

SELECT country_code, country_name 
FROM code_juso
WHERE city_name = #{city_name}
